mapper.update:
UPDATE GNRL_BBS
SET BBS_REMD_CNT = BBS_REMD_CNT + 1
WHERE BBS_SN = #{id}

UPDATE GNRL_BBS
SET BBS_NREMD_CNT = BBS_NREMD_CNT + 1
WHERE BBS_SN = #{id}

mapper.select:
SELECT BBS_REMD_CNT
FROM GNRL_BBS
WHERE BBS_SN = #{id}

SELECT BBS_NREMD_CNT
FROM GNRL_BBS
WHERE BBS_SN = #{id}

mapper.update:
UPDATE GNRL_BBS
SET BBS_SJ = #{updatedBoard.BBS_SJ},
    BBS_CN = #{updatedBoard.BBS_CN},
    GENRE_ID = #{updatedBoard.GENRE_ID}
WHERE BBS_SN = #{id}
